$(document).ready(function(){$("#local_government").change(function(){var a=$(this).val();$("#council_ward").find("option").not(":first").remove(),$.ajax({url:"select-council-ward/"+a,type:"get",dataType:"json",success:function(a){var n=0;if(null!=a.data&&(n=a.data.length),n>0)for(var t=0;t<n;t++){var o="<option value='"+a.data[t].uuid+"'>"+a.data[t].name+"</option>";$("#council_ward").append(o)}}})})});