$(document).ready(function(){$("#state").change(function(){var a=$(this).val();$("#local_government").find("option").not(":first").remove(),$.ajax({url:"select-local-government/"+a,type:"get",dataType:"json",success:function(a){var n=0;if(null!=a.data&&(n=a.data.length),n>0)for(var t=0;t<n;t++){var e="<option value='"+a.data[t].uuid+"'>"+a.data[t].name+"</option>";$("#local_government").append(e)}}})})});